mapper.select:
SELECT * FROM (
    SELECT a.*, ROWNUM rnum
    FROM (
        SELECT * FROM nemember
        ORDER BY signup_date DESC
    ) a
    WHERE ROWNUM <= #{offset} + #{size}
)
WHERE rnum > #{offset}

SELECT * FROM (
    SELECT a.*, ROWNUM rnum
    FROM (
        SELECT * FROM nemember



                    memberid LIKE '%' || #{searchKeyword} || '%'


                    nickname LIKE '%' || #{searchKeyword} || '%'


                    1=1



        ORDER BY signup_date DESC
    ) a
    WHERE ROWNUM <= #{offset} + #{size}
)
WHERE rnum > #{offset}

mapper.update:
UPDATE nemember
grade = #{grade}, name = #{name}, profile = #{profile}, nickname = #{nickname}, email = #{email}, phone = #{phone},
WHERE memberid = #{memberID}

UPDATE neSavProd SET product_name = #{product_name},
product_image = #{product_image},
base_rate = #{base_rate}, goldenball_rate = #{goldenball_rate}, subscription_method = #{subscription_method}, subscription_period = #{subscription_period}, termination_conditions = #{termination_conditions}, minimum_deposit = #{minimum_deposit}, maximum_deposit = #{maximum_deposit}, start_date = #{start_date, jdbcType=DATE}, product_description = #{product_description} WHERE product_code = #{product_code}
